import React, { useState, useEffect } from 'react';
import MaterialTable from 'material-datatable';
import axios from 'axios';

export default function MaterialTable1() {
    
    const [item, setItem] = useState([]);

    const columns = [
      {
       name: "ID Libro",
       field: "id_libro",
       options: {
        filter: true,
        sort: true,
       }
      },
      {
       name: "Titulo",
       field: "titulo",
       options: {
        filter: true,
        sort: false,
       }
      },
      {
        name: 'Autor', 
        field: 'autor'
      },
      {
        name: 'Idioma', 
        field: 'idioma'
      }
    
     ];

  useEffect(() => {
    cargar();
  }, []);


  const cargar = async() =>{
    const { data } = await axios.get("http://localhost:5000/api/todosLibros");

    console.log(data);
    setItem(data.libro);
    return null;
  }

  return (
    <MaterialTable
      title="Tabla de libros"
      columns={columns}
      data={item}
      editable={{
        onRowAdd: (newData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setItem((prevState) => {
                const data = [...prevState.data];
                data.push(newData);
                return { ...prevState, data };
              });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                setItem((prevState) => {
                  const data = [...prevState.data];
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setItem((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
  );
}
