import React, { useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import axios from 'axios';
import MenuItem from '@material-ui/core/MenuItem'



const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));



export default function DialogSelect() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [item, setItem] = useState([]);


  useEffect(() => {
      cargar();
  
    }, []);
  
  
  const cargar = async() =>{
      const { data } = await axios.get("http://localhost:5000/api/todosLibros");
  
      setItem(data.libro);
      return null;
  }

  const handleChange = (event) =>{
      setItem(event.target.value)
  }

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button onClick={handleClickOpen}>Asignar Nuevo Prestamo de Libro</Button>
      <Dialog disableBackdropClick disableEscapeKeyDown open={open} onClose={handleClose}>
        <DialogTitle>Seleccionar libro y Alumno</DialogTitle>
        <DialogContent>
          <form className={classes.container}>
            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="demo-dialog-native">Nombre Libro</InputLabel>
              <Select
                value={item._id}
                OnChange= {handleChange}
              >
                {item.map( items => {
                      return (
                      <MenuItem key={items._id} value={items._id}>{items.titulo}</MenuItem>
                      )
                  })}
              </Select>
            </FormControl>
            <FormControl className={classes.formControl}>
             <InputLabel id="demo-dialog-select-label">Libros</InputLabel>
              <Select
                native
                value={item}
                input={<Input id="demo-dialog-native" />}
              >
                  {item.map( items => {
                      return (
                      <MenuItem key={items._id} value={items._id}>{items.titulo}</MenuItem>
                      )
                  })}
              </Select>
            </FormControl>
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
