import React, { useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import axios from 'axios';
import MenuItem from '@material-ui/core/MenuItem'
import swal from 'sweetalert';



const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

const columns = [
  {
   name: "ID Libro",
   field: "id_libro",
   options: {
    filter: true,
    sort: true,
   }
  },
  {
   name: "ID Alumno",
   field: "titulo",
   options: {
    filter: true,
    sort: false,
   }
  },
  {
    name: 'Fecha Inicio', 
    field: 'fecha_inicio'
  },
  {
    name: 'Fecha Termino', 
    field: 'devolucion'
  },
  {
    name: 'contador', 
    field: 'count'
  },{
    name: "Seleccionar",
    options: {
        headerNoWrap: true,
        customBodyRender: (item, tablemeta, update) => {
            return (
                <div>
                <IconButton
                    variant='outlined'

                    className="btnblock"
                    onClick={() => handleSelecciondelete(item._id)}
                >
                  <DeleteIcon/>
                </IconButton>
                <IconButton
                    variant='outlined'

                    className="btnblock"
                    onClick={() => handleSelecciondelete(item._id)}
                >
                  <EditIcon/>
                </IconButton>
                
                </div>
            );
        },
    },
},

 ];



export default function DialogSelect() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [item, setItem] = useState([]);
  const [item2, setItem2] = useState([]);


  useEffect(() => {
      cargar();
      cargarAlumno();  
  
    }, []);
  
  
  const cargar = async() =>{
      const { data } = await axios.get("http://localhost:5000/api/todosLibros");
  
      setItem(data.libro);
      return null;
  }

  const cargarAlumno = async() => {
    const { data } = await axios.get("http://localhost:5000/api/todosAlumnos");

    setItem2(data.alumno);
    console.log(data)
  }

    // set value for default selection
    const [selectedValue, setSelectedValue] = useState();
    const [selectedValue2, setSelectedValue2] = useState();

  const handleChange = (event) =>{
      setSelectedValue(event.target.value)
  }
  const handleChange2 = (event) =>{
    setSelectedValue2(event.target.value)
}

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    
  };

  const ingresoPrestamo = (data,data2) => {
    setOpen(false);
    console.log(data);
    console.log(data2);
    axios.post("http://localhost:5000/api/guardarPrestamo",data,data2)
    .then(
      (response)=>{
        console.log(response.data);
        swal({ 
            title: "Datos Guardados",
            text: "Se a realizado el prestamo",
            icon: "success",
            button: "Continuar",
        });
      }
    ).catch((error) => {
      console.log(error);
    })
  }


  return (
    <div>
      <Button onClick={handleClickOpen}>Asignar Nuevo Prestamo de Libro</Button>
      <Dialog disableBackdropClick disableEscapeKeyDown open={open} onClose={handleClose}>
        <DialogTitle>Seleccionar libro y Alumno</DialogTitle>
        <DialogContent>
          <form className={classes.container}>
            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="demo-dialog-native">Nombre Libro</InputLabel>
              <Select
              value = {selectedValue}
              onChange={handleChange}
              >
                {item.map( items => {
                      return (
                      <MenuItem key={items._id} value={items._id}>{items.titulo}</MenuItem>
                      )
                  })}
              </Select>
              <h1>{selectedValue}</h1>
            </FormControl>
            <FormControl className={classes.formControl}>
             <InputLabel id="demo-dialog-select-label">Libros</InputLabel>
             <Select
              value = {selectedValue2}
              onChange={handleChange2}
              >
                {item2.map( items => {
                      return (
                      <MenuItem key={items._id} value={items._id}>{items.nombre}</MenuItem>
                      )
                  })}
              </Select>
              <h1>{selectedValue2}</h1>
            </FormControl>
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={()=> ingresoPrestamo(selectedValue2,selectedValue)} color="primary">
            Ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
