import React, { useState, useEffect } from 'react';
import MaterialTable from 'material-datatable';
import axios from 'axios';
import { useForm } from 'react-hook-form';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));


export default function MaterialTable1() {
    
    const { register, handleSubmit, errors } = useForm();
    const classes = useStyles();
    const [item, setItem] = useState([]);

    const columns = [
      {
       name: "ID Libro",
       field: "id_libro",
       options: {
        filter: true,
        sort: true,
       }
      },
      {
       name: "Titulo",
       field: "titulo",
       options: {
        filter: true,
        sort: false,
       }
      },
      {
        name: 'Autor', 
        field: 'autor'
      },
      {
        name: 'Idioma', 
        field: 'idioma'
      }
    
     ];

  const onSubmit = data => {
    axios
    .post("http://localhost:5000/api/guardarLibro", data)
    .then(
      (response)=>{
        console.log(response.data);
        window.location='/menu';
      }
    )
    .catch((error) => {
      console.log(error);
    })
  }
  
  useEffect(() => {
    cargar();
  }, []);


  const cargar = async() =>{
    const { data } = await axios.get("http://localhost:5000/api/todosLibros");

    setItem(data.libro);
    return null;
  }
  console.log(errors);
  return (

     <div> 
         <h1>Ingreso de Libros</h1>
    <form onSubmit={handleSubmit(onSubmit)} className={classes.root}>

      <TextField id="outlined-basic" label="Libro ID" variant="outlined" name="id_libro" inputRef={register} />
      <TextField id="outlined-basic" label="Titulo del libro" variant="outlined" name="titulo" inputRef={register} />
      <TextField id="outlined-basic" label="Autor del libro" variant="outlined" name="autor" inputRef={register} />
      <TextField id="outlined-basic" label="Idioma" variant="outlined" name="idioma" inputRef={register} />

      <Button
        variant="contained"
        color="primary"
        className={classes.button}
        type="submit"
      >
        Enviar
      </Button>
    </form>
        <h1>Biblioteca Virtual</h1>
        <MaterialTable
        title="Libros guardados"
        columns={columns}
        data={item}
        editable={{
            onRowAdd: (newData) =>
            new Promise((resolve) => {
                setTimeout(() => {
                    resolve();
                    setItem((prevState) => {
                    const data = [...prevState.data];
                    data.push(newData);
                    return { ...prevState, data };
                });
                }, 600);
            }),
            onRowUpdate: (newData, oldData) =>
            new Promise((resolve) => {
                setTimeout(() => {
                resolve();
                if (oldData) {
                    setItem((prevState) => {
                    const data = [...prevState.data, newData];
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                    });
                }
                }, 600);
            }),
            onRowDelete: (oldData) =>
            new Promise((resolve) => {
                setTimeout(() => {
                resolve();
                setItem((prevState) => {
                    const data = [...prevState.data];
                    data.splice(data.indexOf(oldData), 1);
                    return { ...prevState, data };
                });
                }, 600);
            }),
        }}
        />
    </div>   

    );

}
