import React, { useState, useEffect } from 'react';
import MaterialTable from 'material-datatable';
import axios from 'axios';
import { useForm } from 'react-hook-form';


export default function MaterialTable1() {
    
    const { register, handleSubmit, errors } = useForm();

    const [item, setItem] = useState([]);

    const columns = [
      {
       name: "ID Libro",
       field: "id_libro",
       options: {
        filter: true,
        sort: true,
       }
      },
      {
       name: "Titulo",
       field: "titulo",
       options: {
        filter: true,
        sort: false,
       }
      },
      {
        name: 'Autor', 
        field: 'autor'
      },
      {
        name: 'Idioma', 
        field: 'idioma'
      }
    
     ];

  useEffect(() => {
    cargar();
  }, []);

  const onSubmit = data => {
    axios
    .post("http://localhost:5000/api/guardarLibro", data)
    .then(
      (response)=>{
        console.log(response.data);
      }
    )
    .catch((error) => {
      console.log(error);
    })
  }

  const cargar = async() =>{
    const { data } = await axios.get("http://localhost:5000/api/todosLibros");

    setItem(data.libro);
    return null;
  }
  console.log(errors);
  return (

     <div> 
         <h1>Ingreso de Libros</h1>
    <form onSubmit={handleSubmit(onSubmit)}>

      <TextField id="outlined-basic" label="Libro ID" variant="outlined" name="id_libro" ref={register} />
      <input type="text" placeholder="Titulo del libro" name="titulo" ref={register} />
      <input type="text" placeholder="Autor del libro" name="autor" ref={register} />
      <input type="text" placeholder="Idioma" name="idioma" ref={register} />

      <input type="submit" />
    </form>

        <MaterialTable
        title="Tabla de libros"
        columns={columns}
        data={item}
        editable={{
            onRowAdd: (newData) =>
            new Promise((resolve) => {
                setTimeout(() => {
                    resolve();
                    setItem((prevState) => {
                    const data = [...prevState.data];
                    data.push(newData);
                    return { ...prevState, data };
                });
                }, 600);
            }),
            onRowUpdate: (newData, oldData) =>
            new Promise((resolve) => {
                setTimeout(() => {
                resolve();
                if (oldData) {
                    setItem((prevState) => {
                    const data = [...prevState.data];
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                    });
                }
                }, 600);
            }),
            onRowDelete: (oldData) =>
            new Promise((resolve) => {
                setTimeout(() => {
                resolve();
                setItem((prevState) => {
                    const data = [...prevState.data];
                    data.splice(data.indexOf(oldData), 1);
                    return { ...prevState, data };
                });
                }, 600);
            }),
        }}
        />
    </div>   

    );

}
